const burger=document.querySelector(".burger"),navLinks=document.querySelector(".nav-links"),navLinksLi=document.querySelectorAll(".nav-links li");burger.addEventListener("click",(()=>{navLinks.classList.toggle("nav-active"),navLinksLi.forEach(((e,n)=>{e.style.animation?e.style.animation="":e.style.animation=`fade 0.5s ease forwards ${n/7+.3}s`})),burger.classList.toggle("toggle")})),window.addEventListener("resize",(()=>{window.innerWidth>=768&&navLinks.classList.remove("nav-active")}));const scrollButton=document.getElementById("scrollButton");scrollButton.addEventListener("click",(()=>{window.scrollTo({top:0,behavior:"smooth"})})),window.addEventListener("scroll",(()=>{window.scrollY>200?scrollButton.classList.add("show"):scrollButton.classList.remove("show")}));